  `url` varchar(255) NOT NULL DEFAULT '',
  `meta_title` varchar(512) NOT NULL DEFAULT '',
  `meta_keywords` varchar(512) NOT NULL DEFAULT '',
  `meta_description` varchar(512) NOT NULL DEFAULT '',
